import numpy as np
import torch
from random import sample

class Feedforward(torch.nn.Module):
    def __init__(self, input_size, hidden_size, output_size):
        super(Feedforward, self).__init__()
        self.input_size = input_size
        self.hidden_size = hidden_size
        self.fc1 = torch.nn.Linear(self.input_size, self.hidden_size)
        self.relu = torch.nn.ReLU()
        self.fc2 = torch.nn.Linear(in_features=self.hidden_size, out_features=output_size)

    def forward(self, x):
        hidden = self.fc1(x)
        relu = self.relu(hidden)
        output = self.fc2(relu)
        return output

    def predict(self, x):
        hidden = self.fc1(torch.tensor(x))
        relu = self.relu(hidden)
        output = self.fc2(relu)
        return np.argmax(output.tolist())


def get_batch(inputs, labels, batch_size):
    indices = sample(batch_size, list(range(len(inputs))))
    return inputs[indices], labels[indices]


